mapper.select:
select p.id id, p.board_uri board_uri, post_title, post_writer,post_hit, post_content,post_notice,post_category,p.post_regiDate post_regiDate
from post p,post_notice pn
where p.id = pn.post_id and pn.board_uri = #{boardUri}
order by pn.post_regiDate desc;

select * from post p where 1 = 1
and post_category in ( select pc.category_menu from post_category pc where pc.category_menu = #{category} and p.board_uri = pc.board_uri )
and board_uri=#{boardUri} and available= true order by id desc limit #{beginPage},#{perPageSize}

select *
from post
where board_uri = #{boardUri} and id = #{id}

mapper.update:
update post
set post_title = #{postTitle},
    post_content = #{postContent},
    post_regiDate = #{postRegiDate}
where board_uri = #{boardUri} and id = #{id} and post_writer=#{postWriter}

mapper.select:
select count(*) from post where 1=1
and post_category in ( select pc.category_menu from post_category pc where pc.category_menu = #{category} and pc.board_uri = board_uri)
and board_uri=#{boardUri}

select count(*)
from post
where board_uri = #{boardUri}

mapper.update:
update post
set post_hit = post_hit +1
where id = #{id}
